mapper.select:
select

from t_user
where 1=1

    AND name = #{name}


    AND code = #{code}


    AND  birthday = #{birthday}


    AND sex = #{sex}


    AND  pwd = #{pwd}


    AND  identity_code = #{identityCode}


    AND mob_phone = #{mobPhone}


    AND home_phone = #{homePhone}


    AND address = #{address}


    AND  ost_code = #{ostCode}


    AND qopen_id = #{qopenId}


    AND  email = #{email}


    AND  seq_no = #{seqNo}


    AND  create_time = #{createTime,jdbcType=TIMESTAMP}


    AND  status = #{status,jdbcType=BIT}


    AND  remark = #{remark}


    AND wopen_id = #{wopenId}


    AND community_id = #{communityId,jdbcType=BIGINT}


    AND image_url = #{imageUrl}


    AND city_id = #{cityId,jdbcType=BIGINT}
